import React, { useState } from 'react';
import axios from 'axios';
import './login.css'; // Make sure to import the login.css file

export const Login = () => {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");

    function handleHover(event) {
        event.target.classList.add("button-hover");
    }

    function handleMouseLeave(event) {
        event.target.classList.remove("button-hover");
    }

    function handleLogin() {
        const login = {
            username,
            password,
        };
        axios
            .post("http://localhost:5000/login", login)
            .then((response) => {
                if (response.data.success) {
                    alert("Logged in successfully.");
                    localStorage.setItem("auth-token", response.data.accessToken);
                    // Redirect or navigate to the home page
                } else {
                    alert(response.data.error);
                }
            })
            .catch((error) => {
                console.log(error);
                alert("Couldn't login");
            });
    }

    return (
      <div className="container">
          <div className="screen">
              <div className="screen__content">
                  <div className="cont">
                      <div className="login__field">
                          <i className="bi bi-person-fill login__icon"></i>
                          <input
                              className="login__input"
                              type="text"
                              onChange={(e) => setUsername(e.target.value)}
                              placeholder="Username"
                          />
                      </div>
                      <div className="login__field">
                          <i className="bi bi-lock-fill login__icon"></i>
                          <input
                              className="login__input"
                              type="password"
                              onChange={(e) => setPassword(e.target.value)}
                              placeholder="Password"
                          />
                      </div>
                      <button
                          type="submit"
                          onMouseEnter={handleHover}
                          onMouseLeave={handleMouseLeave}
                          onClick={handlelogin}
                          className="login__submit"
                      >
                          <span className="button__icon bi bi-box-arrow-in-right"></span>
                      </button>
                  </div>
              </div>
              <div className="screen__background">
                  <div className="screen__background__shape screen__background__shape1"></div>
                  <div className="screen__background__shape screen__background__shape2"></div>
                  <div className="screen__background__shape screen__background__shape3"></div>
                  <div className="screen__background__shape screen__background__shape4"></div>
              </div>
          </div>
      </div>
  );
  
};
