import React, { useState } from 'react';
import axios from 'axios';
import './login.css'; // Make sure to import the login.css file

export const Login = () => {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");

    function handleHover(event) {
        event.target.classList.add("button-hover");
    }

    function handleMouseLeave(event) {
        event.target.classList.remove("button-hover");
    }

    function handleLogin() {
        const login = {
            username,
            password,
        };
        axios
            .post("http://localhost:5000/login", login)
            .then((response) => {
                if (response.data.success) {
                    alert("Logged in successfully.");
                    localStorage.setItem("auth-token", response.data.accessToken);
                    // Redirect or navigate to the home page
                } else {
                    alert(response.data.error);
                }
            })
            .catch((error) => {
                console.log(error);
                alert("Couldn't login");
            });
    }

    return (
        <div className="container">
            <div className="cont">
                <h2>Login</h2>
                <div className="">
                    <label>Username:</label><br />
                    <input type="text" onChange={(e) => setUsername(e.target.value)} />
                </div>
                <div className="">
                    <label>Password:</label><br />
                    <input type="password" onChange={(e) => setPassword(e.target.value)} />
                </div>
                <button
                    type="submit"
                    onMouseEnter={handleHover}
                    onMouseLeave={handleMouseLeave}
                    onClick={handleLogin}
                    className="submitbut"
                >
                    Submit
                </button>
            </div>
        </div>
    );
};
