import React, { useState } from 'react';
import axios from 'axios';
import './login.css'; // Import the login.css file

export const Login = () => {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");

    function handleHover(event) {
        event.target.classList.add("button-hover");
    }

    function handleMouseLeave(event) {
        event.target.classList.remove("button-hover");
    }

    function handlelogin() {
        const login = {
            username,
            password,
        };
        axios
            .post("http://localhost:5000/login", login)
            .then((response) => {
                if (response.data.success) {
                    alert("Logged in successfully.")
                    localStorage.setItem("auth-token", response.data.accessToken);
                    navigate('/Home');
                } else {
                    alert(response.data.error);
                }
            })
            .catch((error) => {
                console.log(error);
                alert("Couldn't login");
            });
    }

    return (
        <div className="cont text-center m-4">
            <div className=" ">
                <label>Username:</label><br />
                <input className="m-2 login__input" type="text" onChange={(e) => setUsername(e.target.value)} />
            </div>
            <div className=" m-5 ">
                <label>Password:</label><br />
                <input className="m-2 login__input" type="password" onChange={(e) => setPassword(e.target.value)} />
            </div>
            <button
                type="submit"
                onMouseEnter={handleHover}
                onMouseLeave={handleMouseLeave}
                onClick={handlelogin}
                className="m-4 p-6 submitbut login__submit"
            >
                Submit
            </button>
        </div>
    );
};
